import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = 'http://localhost:5000/api';

function App() {
  const [employees, setEmployees] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [statistics, setStatistics] = useState({});
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    position: '',
    department: '',
    salary: '',
    hire_date: '',
    phone: '',
    address: ''
  });

  useEffect(() => {
    fetchEmployees();
    fetchStatistics();
  }, []);

  const showMessage = (text, type = 'success') => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 3000);
  };

  const fetchEmployees = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE}/employees`);
      setEmployees(response.data.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      showMessage('Failed to load employees', 'error');
    } finally {
      setLoading(false);
    }
  };

  const fetchStatistics = async () => {
    try {
      const response = await axios.get(`${API_BASE}/statistics`);
      setStatistics(response.data);
    } catch (error) {
      console.error('Error fetching statistics:', error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      if (editingEmployee) {
        await axios.put(`${API_BASE}/employees/${editingEmployee.id}`, formData);
        showMessage('Employee updated successfully!');
      } else {
        await axios.post(`${API_BASE}/employees`, formData);
        showMessage('Employee added successfully!');
      }
      resetForm();
      fetchEmployees();
      fetchStatistics();
    } catch (error) {
      console.error('Error saving employee:', error);
      showMessage('Error saving employee. Please try again.', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (employee) => {
    setFormData({
      name: employee.name,
      email: employee.email,
      position: employee.position,
      department: employee.department,
      salary: employee.salary,
      hire_date: employee.hire_date.split('T')[0],
      phone: employee.phone || '',
      address: employee.address || ''
    });
    setEditingEmployee(employee);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      setLoading(true);
      try {
        await axios.delete(`${API_BASE}/employees/${id}`);
        showMessage('Employee deleted successfully!');
        fetchEmployees();
        fetchStatistics();
      } catch (error) {
        console.error('Error deleting employee:', error);
        showMessage('Error deleting employee', 'error');
      } finally {
        setLoading(false);
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      position: '',
      department: '',
      salary: '',
      hire_date: '',
      phone: '',
      address: ''
    });
    setEditingEmployee(null);
    setShowForm(false);
  };

  const formatCurrency = (amount) => {
    return '₹' + amount.toLocaleString('en-IN');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN');
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Employee Management System</h1>
        <p>Manage your employee records efficiently</p>
      </header>

      <div className="container">
        {message && (
          <div className={`message ${messageType === 'error' ? 'error-message' : 'success-message'}`}>
            {message}
          </div>
        )}

        {/* Statistics Dashboard */}
        <div className="statistics">
          <h2>Dashboard Overview</h2>
          <div className="stats-grid">
            <div className="stat-card">
              <h3>Total Employees</h3>
              <p>{statistics.totalEmployees?.count || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Departments</h3>
              <p>{statistics.totalDepartments?.count || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Average Salary</h3>
              <p>{statistics.avgSalary?.average ? formatCurrency(statistics.avgSalary.average) : '₹0'}</p>
            </div>
          </div>
        </div>

        {/* Employee Form */}
        {showForm && (
          <div className="employee-form">
            <h2>{editingEmployee ? 'Edit Employee' : 'Add New Employee'}</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                <input
                  type="text"
                  name="name"
                  placeholder="Full Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-row">
                <input
                  type="text"
                  name="position"
                  placeholder="Position"
                  value={formData.position}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="text"
                  name="department"
                  placeholder="Department"
                  value={formData.department}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-row">
                <input
                  type="number"
                  name="salary"
                  placeholder="Salary"
                  value={formData.salary}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="date"
                  name="hire_date"
                  value={formData.hire_date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleInputChange}
              />
              <textarea
                name="address"
                placeholder="Address"
                value={formData.address}
                onChange={handleInputChange}
                rows="3"
              />
              <div className="form-buttons">
                <button type="submit" className="btn btn-primary">
                  {editingEmployee ? 'Update Employee' : 'Add Employee'}
                </button>
                <button type="button" className="btn btn-secondary" onClick={resetForm}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Employee List */}
        <div className="employee-list">
          <div className="list-header">
            <h2>Employees</h2>
            <button 
              className="btn btn-primary" 
              onClick={() => setShowForm(true)}
            >
              Add New Employee
            </button>
          </div>
          
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Position</th>
                  <th>Department</th>
                  <th>Salary</th>
                  <th>Hire Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {employees.map(employee => (
                  <tr key={employee.id}>
                    <td>
                      <div className="employee-info">
                        <strong>{employee.name}</strong>
                        <small>{employee.email}</small>
                      </div>
                    </td>
                    <td>{employee.position}</td>
                    <td>{employee.department}</td>
                    <td>{formatCurrency(employee.salary)}</td>
                    <td>{formatDate(employee.hire_date)}</td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          className="btn btn-edit"
                          onClick={() => handleEdit(employee)}
                        >
                          Edit
                        </button>
                        <button 
                          className="btn btn-delete"
                          onClick={() => handleDelete(employee.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
